mapper.select:
select '中华人民共和国不动产权证书' zzlxmc,'11100000MB03271699001' zzlxdm, a.zzbh,a.zzbsm zzbs,a.zh zh,a.djyy bfyy,b.syqqssj zzyxqssj,b.syqjssj zzyxjzsj, a.djjg bfjg , a.djjgdm bfjgdm ,a.zzzxrq zxrq ,a.zxyy zxyy , a.zzfl zzfl from dc_cert_info a left join dc_right_info b on a.zzbh = b.zzbh
and a.id = #{id}
and rownum=1

select to_char(bfsj,'yyyy-MM-dd') sj, sum(case when zzfl=1 then '1' else '0' end) cert, sum(case when zzfl=2 then '1' else '0' end) prove, sum(case when zzfl!=1 and zzfl!=2 then '1' else '0' end) other from dc_cert_info
and djjgdm = #{djjgdm}
and bfsj >= #{startDate}
and #{endDate} >= bfsj
group by to_char(bfsj,'yyyy-MM-dd') order by to_char(bfsj, 'yyyy-MM-dd')

SELECT ER.MC AS QLRMC,ER.ZJH AS QLRZJH,INFO.ZH AS BDCQZH,INFO.ZZMC AS ZZMC,INFO.BFSJ AS BFSJ,
INFO.DJJG AS QQBFDJJG,INFO.DJYY AS BFYY,INFO.ZT AS ZT FROM DC_CERT_INFO INFO , DC_RIGHTHOLDER ER
WHERE INFO.ZZBH = ER.ZZBH

    AND INFO.ZH = #{zh}


    AND ER.MC = #{qlrmc}


    AND ER.ZJH = #{qlrzjh}
